<?phpif(isset($_POST['submit'])){	$id= trim($_POST['id']);	$pass= trim($_POST['pass']);	$cpass= trim($_POST['cpass']);	$name= trim($_POST['name']);	$type= trim($_POST['type']);		if($id==""||  $pass==""||   $cpass==""||  $name==""||  $type=="" )	{		echo "Fillup all the information first";			}	else if( $pass!=$cpass )	{		echo "comfirm Password doesnt match with the Password";	}	else	{		$servername="localhost";		$username="root";		$password="";		$dbname="user";				$conn=mysqli_connect($servername,$username,$password,$dbname);				if(!$conn)		{			die("connection error".mysqli_connect_error());		}		else		{			echo "connected";		}				$sql="insert into registration values('$id','$pass','$name','$type')";				$result=mysqli_query($conn,$sql);				header('location:login.php');				mysqli_close($conn);			}}?><center><form action="#" method="POST">	<table border="0" cellspacing="0" cellpadding="0">		<tr>			<td>				<fieldset>					<legend><h3>REGISTRATION</h3></legend>					Id<br/><input type="text" name="id"><br/>					Password<br/><input type="password" name="pass"><br/>					Confirm Password<br/><input type="password" name="cpass"><br/>					Name<br/><input type="text" name="name"><br/>					User Type<hr/>					<input type="radio" name="type" value="user"/>User					<input type="radio" name="type" value="admin"/>Admin					<hr/>					<input type="submit" name="submit" value="Sign Up">					<a href="login.php">Sign In</a>				</fieldset>			</td>		</tr>                                	</table></form></center>		